import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

const REACT_APP_FIREBASE_API_KEY= process.env.REACT_APP_FIREBASE_API_KEY
const REACT_APP_FIREBASE_AUTH_DOMAIN= process.env.REACT_APP_FIREBASE_AUTH_DOMAIN
const REACT_APP_FIREBASE_DATABASE_URL= process.env.REACT_APP_FIREBASE_DATABASE_URL
const REACT_APP_FIREBASE_PROJECT_ID= process.env.REACT_APP_FIREBASE_PROJECT_ID
const REACT_APP_FIREBASE_STORAGE_BUCKET= process.env.REACT_APP_FIREBASE_STORAGE_BUCKET
const REACT_APP_FIREBASE_MESSAGE_SENDER_ID= process.env.REACT_APP_FIREBASE_MESSAGE_SENDER_ID
const REACT_APP_FIREBASE_APP_ID= process.env.REACT_APP_FIREBASE_APP_ID
const REACT_APP_FIREBASE_MEASUREMENT_ID= process.env.REACT_APP_FIREBASE_MEASUREMENT_ID

const config = {
  apiKey: 'AIzaSyAh7PEw_HfEOWu5XD6ObOzbyVv3Z2ZPJos',
  authDomain: 'crwn-clothing-udemy-70686.firebaseapp.com',
  databaseURL: 'https://crwn-clothing-udemy-70686.firebaseio.com',
  projectId: 'crwn-clothing-udemy-70686',
  storageBucket: 'crwn-clothing-udemy-70686.appspot.com',
  messagingSenderId: '1077327368780',
  appId: '1:1077327368780:web:d300894f82bd2fc4b1dae9',
  measurementId: 'G-KX579YSVF0',
};

export const createUserProfileDocument = async (userAuth, additionalData) => {
  if (!userAuth) return;

  const userRef = firestore.doc(`users/${userAuth.uid}`);

  const snapShot = await userRef.get();

  if (!snapShot.exists) {
    const { displayName, email } = userAuth;
    const createdAt = new Date();

    try {
      await userRef.set({
        displayName,
        email,
        createdAt,
        ...additionalData,
      });
    } catch (error) {
      console.log('error createing user', error.message);
    }
  }
  return userRef;
};

firebase.initializeApp(config);

export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({ prompt: 'select_account' });
export const signInWithGoogle = () => auth.signInWithPopup(provider);

export default firebase;
