import styled, { css } from 'styled-components';

export const CheckoutItemContainer = styled.div`
  width: 100%;
  display: flex;
  min-height: 100px;
  border-bottom: 1px solid darkgrey;
  padding: 15px 0;
  font-size: 20px;
  align-items: center;
`;

export const ImageContainer = styled.div`
  width: 23%;
  padding-right: 15px;

  img {
    width: 100%;
    height: 100%;
  }
`;

export const ImageStyles = styled.img`
  width: 100%;
  height: 100%;
`;

const ItemPropertytyles = css`
  width: 23%;
`;

export const ItemNameAndPrice = styled.span`
  ${ItemPropertytyles}
`;

export const QuantityStyles = styled.span`
  ${ItemPropertytyles};
  display: flex;
`;

export const ValueStyles = styled.span`
  margin: 0px 10px;
`;

export const ArrowStyles = styled.div`
  cursor: pointer;
`;

export const RemoveButtonStyles = styled.div`
  padding-left: 12px;
  cursor: pointer;
`;
