import { createSelector } from 'reselect';

// const COLLECTION_ID_MAP = {
//   hats: 1,
//   sneakers: 2,
//   jackets: 3,
//   womens: 4,
//   mens: 5,
// };

const selectShop = (state) => state.shop;

export const selectCollections = createSelector([selectShop], (shop) => shop.collections);

//find collection.id matching the url paramenter of our collection id map
// export const selectCollection = (collectionUrlParam) =>
//   createSelector([selectCollections], (collections) =>
//     collections.find((collection) => collection.id === COLLECTION_ID_MAP[collectionUrlParam])
//   );

//Storing list of elements inside na object instead of na array  data normalization (shop.data was changed from array to obj)
//this way is as fast 
export const selectCollection = (collectionUrlParam) =>
  createSelector([selectCollections], (collections) => collections[collectionUrlParam]);
