import React from 'react';
import { connect } from 'react-redux';

import { addItem, removeItem, clearItemFromCart } from '../../redux/cart/cart.actions';

import {
  CheckoutItemContainer,
  ImageContainer,
  ItemNameAndPrice,
  QuantityStyles,
  ValueStyles,
  ArrowStyles,
  RemoveButtonStyles,
} from './checkout-item.styles';

const CheckoutItem = ({ cartItem, clearItem, addItem, removeItem }) => {
  const { name, imageUrl, price, quantity } = cartItem;

  return (
    <CheckoutItemContainer>
      <ImageContainer>
        <ImageStyles src={imageUrl} alt="item" />
      </ImageContainer>
      <ItemNameAndPrice>{name}</ItemNameAndPrice>
      <QuantityStyles>
        <ArrowStyles onClick={() => removeItem(cartItem)}>&#10094;</ArrowStyles>
        <ValueStyles>{quantity}</ValueStyles>
        <ArrowStyles onClick={() => addItem(cartItem)}>&#10095;</ArrowStyles>
      </QuantityStyles>
      <ItemNameAndPrice>{price}</ItemNameAndPrice>
      {/* UTF-8 Dingbats are characters displayed in HTML '&#10005;' */}
      <RemoveButtonStyles onClick={() => clearItem(cartItem)}>&#10005;</RemoveButtonStyles>
    </CheckoutItemContainer>
  );
};

const mapDispatchToProps = (dispatch) => ({
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  addItem: (item) => dispatch(addItem(item)),
  removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
