import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

const config = {
  apiKey: process.env.NODE_ENV_FIREBASE_API_KEY,
  authDomain: process.env.NODE_ENV_FIREBASE_AUTH_DOMAIN,
  databaseURL: process.env.NODE_ENV_FIREBASE_DATABASE_URL,
  projectId: process.env.NODE_ENV_FIREBASE_STORAGE_BUCKET,
  storageBucket: process.env.NODE_ENV_FIREBASE_PROJECT_ID,
  messagingSenderId: process.env.NODE_ENV_FIREBASE_MESSAGE_SENDER_ID,
  appId: process.env.NODE_ENV_FIREBASE_APP_ID,
  measurementId: process.env.NODE_ENV_FIREBASE_MEASUREMENT_ID,
};

firebase.initializeApp(config);

export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
provider.setCustomParameters({ prompt: 'select_account' });
export const signInWithGoogle = () => auth.signInWithPopup(provider);

export default firebase;
